// Require jQuery
require(['jQueryNoConflict'], function($) {

  // Waiting jQuery to be ready
  $(document).ready(function() {

    // Usefull variables
    var iframe = $("#iframe")[0];
    var autoSync = $("#autosync");
    var variableInputs = $("#bt-variables input");

    // Function that waits the iframe to be fully loaded
    var waitUntilIFrameIsLoaded = function(callback) {
      var iframeHead = $(iframe).contents().find("head")[0];
      if (iframeHead !== undefined) {
        callback();
      } else {
        setTimeout(function(){ waitUntilIFrameIsLoaded(callback); }, 100);
      }
    };

    // Function that add LESS scripts to the iframe document
    var initIframe = function() {
      var iframeHead = $(iframe).contents().find("head")[0];
      var link = document.createElement("link");
      link.rel = "stylesheet/less";
      link.type = "text/css";
      link.href = "/xwiki/bin/skin/skins/flamingo/less/style.less.vm";
      /*var script1 = document.createElement("script");
      script1.text = "less = {env: \"production\"};";*/
      var script2 = document.createElement("script");
      script2.src = "$services.webjars.url('less/1.7.0/less.min.js')";
      iframeHead.appendChild(link);
      //iframeHead.appendChild(script1);
      iframeHead.appendChild(script2);
    };

    // Refresh the preview
    var refreshPreview = function () {
      var varMap = {};
      for (var i=0; i<variableInputs.length; ++i) {
        var variableInput = variableInputs[i];
        var name = '@' + variableInput.id;
        varMap[name] = variableInput.value;
      }
      iframe.contentWindow.less.modifyVars(varMap);
    };

    // Initialize the iframe
    waitUntilIFrameIsLoaded(initIframe);
    // Intiialize the event handlers
    $("#refresh").click(refreshPreview);

  });

});
